CREATE TABLE IF NOT EXISTS `{PREFIX}plugins_goodslocation` (`id` int(11) unsigned NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT '自增id', `good_id` char(60) NOT NULL DEFAULT '' COMMENT '商品编号', `lon` float(10,6) NOT NULL COMMENT '经度 lon BD-09', `lat` float(10,6) NOT NULL COMMENT '纬度 lat BD-09', `location` varchar(255) NOT NULL DEFAULT '' COMMENT '位置信息', `upd_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间') DEFAULT CHARSET={CHARSET} AUTO_INCREMENT=1;